import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import '../App.css';
import { CardComponent } from '../components/CardComponent';
import { CHARACTER_LIST } from '../constants/URLConstants';
import { CharaListAction, FavouriteListAction } from '../redux/action/action-creator';
import { NavBar } from '../router/NavBar';

export const Home = () => {



  const { isLoading, favouriteList, charList } = useSelector((state: any) => state.mainReducer);

  const dispatch = useDispatch();


  const [characterList, setcharacterList] = useState<any>([]);
  const [favList, setfavList] = useState<any>([]);
  console.log("ðŸš€ ~ file: Home.tsx ~ line 20 ~ Home ~ favList", favList)


  useEffect(() => {
    fetch(
      CHARACTER_LIST)
      .then((res) => res.json())
      .then((res) => {
        console.log("ðŸš€ ~ file: Home.tsx ~ line 28 ~ .then ~ res", res)
        var newData = [...res];
        newData.forEach(function (file) {
          file.selectedItem = false
        })
        setcharacterList(newData)
        dispatch(CharaListAction(characterList));
      })
  }, [])

  // useEffect(() => {

  //   if (favList) {
  //     dispatch(FavouriteListAction(favList));
  //   }

  // }, [favList])




  const onClickLike = (item: any, index: number) => {


    let newCharList = [...characterList];
    newCharList[index].selectedItem = !item.selectedItem;

    setcharacterList(newCharList);
    var newArray = [...newCharList];

    if (newCharList[index].selectedItem) {
      newArray = newCharList.filter(function (item: any) {
        return item.selectedItem == true;
      });
      setfavList(newArray);
    } else {
      newArray = newCharList.filter(function (item: any) {
        return item.selectedItem == true;
      });
      setfavList(newArray);
    }
      dispatch(FavouriteListAction(favList));


  }

  const mainView = {
    paddingLeft: '110px'
  }

  return (
    <>
      <NavBar
        onChangeText={(text: string) => {
        }}
      />

      <div className="df" style={mainView}>
        {characterList.map((item: object, index: number) => {
          return (
            <CardComponent
              item={item}
              index={index}
              onClickFav={(item: any, index: number) => {
                onClickLike(item, index);
              }}
            />
          )
        })}
      </div>
    </>
  )
}
