import { SHOW_ARRAY_FAILURE, SHOW_ARRAY_REQUEST, SHOW_ARRAY_SUCCESS } from "../action/action-typers"


const initialState = {
    isLoading: false,
}




export const mainReducer = (state = initialState, action: any) => {
    switch (action.type) {

        case SHOW_ARRAY_REQUEST:
            return { ...state, isLoading: "empty data" }
        // case SHOW_ARRAY_SUCCESS:
        //     return { ...state, isLoading: false, }
        // case SHOW_ARRAY_FAILURE:
        //     return { ...state, isLoading: false }





        default:
            return state
    }
}
