import { CHARACHTER_ARRAY_REQUEST, FAVOURITE_LIST, SHOW_ARRAY_FAILURE, SHOW_ARRAY_REQUEST, SHOW_ARRAY_SUCCESS } from "../action/action-typers"


const initialState = {
    isLoading: false,
    favouriteList: []
}




export const mainReducer = (state = initialState, action: any) => {
    switch (action.type) {

        case CHARACHTER_ARRAY_REQUEST:
            console.log("enter here");
            return { ...state, isLoading: "empty data" }
        // case SHOW_ARRAY_SUCCESS:
        //     return { ...state, isLoading: false, }
        // case SHOW_ARRAY_FAILURE:
        //     return { ...state, isLoading: false }


        case FAVOURITE_LIST:
            console.log("action.payload", action.payload);

            return { ...state, favouriteList: action.payload }



        default:
            return state
    }
}
