import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import '../App.css';
import { CardComponent } from '../components/CardComponent';
import { CHARACTER_LIST } from '../constants/URLConstants';
import { FavouriteListAction } from '../redux/action/action-creator';
import { SHOW_ARRAY_REQUEST } from '../redux/action/action-typers';
import { NavBar } from '../router/NavBar';

export const Home = () => {



  const { isLoading, favouriteList } = useSelector((state: any) => state.mainReducer);
  console.log("ðŸš€ ~ file: Home.tsx ~ line 15 ~ Home ~ favouriteList", favouriteList)

  const dispatch = useDispatch();


  const [characterList, setcharacterList] = useState<any>([])

  useEffect(() => {
    fetch(
      CHARACTER_LIST)
      .then((res) => res.json())
      .then((res) => {
        var newData = [...res];
        newData.forEach(function (file) {
          file.selectedItem = false
        })
        setcharacterList(newData)
      })
  }, [])



  const onClickLike = (item: any, index: number) => {
    console.log("item.selectedItem", item.selectedItem);

    if (item.selectedItem) {
      var newArray = characterList.filter(function (item: any) {
        return item.selectedItem == true;
      });
      console.log("11", newArray);
      dispatch(FavouriteListAction(newArray));
      characterList[index].selectedItem = false;
    } else {
      var newArray = characterList.filter(function (item: any) {
        return item.selectedItem == true;
      });
      console.log("11", newArray);
      dispatch(FavouriteListAction(newArray));
      characterList[index].selectedItem = true;
    }

  }

  const mainView = {
    paddingLeft: '110px'
  }

  return (
    <>
      <NavBar
        onChangeText={(text: string) => {
        }}
      />

      <div className="df" style={mainView}>
        {characterList.map((item: object, index: number) => {
          return (
            <CardComponent
              item={item}
              index={index}
              onClickFav={(item: any, index: number) => {
                onClickLike(item, index);
                dispatch({ type: SHOW_ARRAY_REQUEST });
              }}
            />
          )
        })}
      </div>
    </>
  )
}
