import { CHARACHTER_LIST, FAVOURITE_LIST, REMOVE_FAVOURITE_LIST, } from "../action/action-typers"


let initialState = {
    isLoading: false,
    favouriteList: [],
    charList: []
}




export const mainReducer = (state = initialState, action: any) => {
    switch (action.type) {

        case CHARACHTER_LIST:
            console.log("CHARACHTER_LIST", action.payload);
            return {
                ...state, charList: action.payload
            }


        case FAVOURITE_LIST:
            console.log("FAVOURITE_LIST", action.payload);
            return {
                ...state,
                favouriteList: action.payload
                // favouriteList: [...state.favouriteList, action.payload],
            }
        case REMOVE_FAVOURITE_LIST:
            return {
                ...state,
                favouriteList: action.payload
            }

        default:
            return state
    }
}
