      //   return item.selectedItem == true;
      // });
      // console.log("11", newArray);
      // dispatch(FavouriteListAction(newArray));
      characterList[index].selectedItem = true;
      console.log("item.selectedItem", item.selectedItem);
    }

  }


  return (
    <>
      <NavBar
        onChangeText={(text: string) => {
        }}
      />

      <div className="df" style={mainView}>
        {favouriteList.map((item: object, index: number) => {
          return (
            <CardComponent
              item={item}
              index={index}
              onClickFav={(item: any, index: number) => {
                onClickLike(item, index);
              }}
            />
          )
        })}
      </div>
    </>
  )
}
